import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Profile from './components/Profile'
import './App.css';

function App() {

  const [count, setCount] = useState(1);
  const [users, setUsers] = useState([]);


  useEffect(() => {
     axios
     .get(`https://jsonplaceholder.typicode.com/users/${count}`)
     .then((response) => {
        setUsers(response.data)
      })
     .catch((err) => {
       console.log(err)
     })
  }, [count])

  const isOverTen = () => {
        return count > 10
  }


  return (
    <div className="container m-2">
      <ul>
        <h1 className="text-center">MY APP</h1>
         {
        isOverTen() && <div className="alert alert-danger">STOP !!!</div>
      }
        <button className="btn btn-info mb-5"onClick={() => setCount(count + 1)}>USER {count}</button>
        <Profile count={count}
                 name={users.name}
                 username={users.username}
                 email={users.email}
                 website={users.website}
                                           />
      </ul>
    </div>
  )
}

export default App;
