import React, { useState, useEffect, Fragment } from 'react';
import axios from 'axios';

const UserList = () => {
    const [count, setCount] = useState(1);
    const [listOfUSer, setlistOfUSer] = useState(['']);
    
    
    useEffect(() => {
        axios
        .get(`'https://jsonplaceholder.typicode.com/users/'${count}`)
        .then((response) => {
            setlistOfUSer(response.data)
        })
        
    }, [count]);


    const ListeUser = listOfUSer.map((user) => <Fragment key={user.id}>
      <li className="list-group-item h3">USER ID:{count} </li>
      <li className="list-group-item">name: {user.name}</li>
      <li className="list-group-item"><strong>username:</strong> {user.username}</li>
      <li className="list-group-item"><strong>email:</strong> {user.email}</li>
      <li className="list-group-item"><strong>website:</strong> {user.website}</li>
    </Fragment>
             )


    const isOverTen = () => {
        return count > 10 
    };
    

return (
       <div className="container mt-3">
          <h1 className="text-center bg-dark text-white">KIMO APP</h1>
            {
              isOverTen() && <div className="alert alert-danger">STOP !!!</div>
            }
              <button className="btn btn-info mt-5 mb-2" onClick={() => setCount(count + 1)}>USER {count}</button>

           <ul>
           {ListeUser}
           </ul>
       
       
       </div>
  )
}

export default UserList;
