import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserList = () => {
    const [count, setCount] = useState(1);
    const [listOfUSer, setlistOfUSer] = useState({});
    
    
    useEffect(() => {
        axios
        .get(`https://jsonplaceholder.typicode.com/users/${count}`)
        .then((response) => {
            setlistOfUSer(response.data)
        })
        .catch((err) => {
            console.log(err)
        })
    }, [count]);


    const isOverTen = () => {
        return count > 10 
    };
    

return (
       <div className="container mt-3">
          <h1 className="text-center bg-dark text-white">KIMO APP</h1>
            {
              isOverTen() && <div className="alert alert-danger">STOP !!!</div>
            }
              <button className="btn btn-info mt-5 mb-2" onClick={() => setCount(count + 1)}>USER {count}</button>
             <ul className="list-group">
                 <li className="list-group-item"><strong>USER ID:</strong>{}</li>
               <li className="list-group-item"><strong>name:</strong> {listOfUSer.name}</li>
               <li className="list-group-item"><strong>username:</strong> {listOfUSer.username}</li>
               <li className="list-group-item"><strong>email:</strong> {listOfUSer.email}</li>
               <li className="list-group-item"><strong>website:</strong> {listOfUSer.website}</li>
             </ul>
       </div>
  )
}

export default UserList;
