import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UserList from './components/UserList'
import './App.css';

function App() {

  const [count, setCount] = useState(1);
  const [listOfUSer, setlistOfUSer] = useState({});


  useEffect(() => {
     axios
     .get(`https://jsonplaceholder.typicode.com/users/${count}`)
     .then((response) => {
        setlistOfUSer(response.data)
      })
     .catch((err) => {
       console.log(err)
     })
  }, [count]);


  const isOverTen = () => {
        return count > 10 
  };




  return (
    <div className="container m-3">
      <ul>
        <h1 className="text-center bg-dark text-white">KIMO APP</h1>
         {
           isOverTen() && <div className="alert alert-danger">STOP !!!</div>
         }
        <button className="btn btn-info mb-5"onClick={() => setCount(count + 1)}>USER {count}</button>
        { listOfUSer.map(<UserList count={count}
                                   name={listOfUSer.name}
                                   username={listOfUSer.username}
                                   email={listOfUSer.email}
                  website={listOfUSer.website}
                                                />) 
        }
      </ul>
    </div>
  )
}

export default App;
